import React from "react"
// import store from '../../../store';
// import * as types from '../../../store/action';
// import { types } from "@babel/core";
export default class list extends React.Component{
    constructor(){
        super();
        this.state = {number:store.getState().counter.number}
    }
    componentWillMount(){
        this.unsubscribe = store.subscribe(()=>{
            this.setState({number:store.getState().counter.number})
        })
    }
    componentWillUnmount(){
        this.unsubscribe();//取消订阅
    }
    render(){
        return(
            <div>
               <p>{this.state.number}</p><br/> 
               <button onClick={()=>store.dispatch({type:types.INCREMENT})}>+</button> <br/> 
               <button onClick={()=>store.dispatch({type:types.DECREMENT})}>-</button> <br/>  
               <button onClick={()=>{
                   setTimeout(()=>{
                      store.dispatch({type:types.INCREMENT})  
                   },1000)
               }}>异步加</button>  <br/> 
            </div>
        )
    }
}
