import React from "react"
import {Link} from "react-router-dom"
import './footNav.less'
// import imgHome from "../../../static/images/tabBar/home.png"
import imgHome from "../../static/images/tabBar/home.png"
// import imgHomeActive from "../../static/images/tabBar/home-active.png"
// import imgLottery from "../../static/images/tabBar/lottery.png"
// import imgLotteryActive from "../../static/images/tabBar/lottery-active.png"
// import imgList from "../../static/images/tabBar/list.png"
// import imgListActive from "../../static/images/tabBar/list-active.png"
// import imgme from "../../static/images/tabBar/list.png"
// import imgmeActive from "../../static/images/tabBar/me-active.png"
const tabbarArr = [
    {   id:0,
        text:"Home",
        imgPic:"imgHome",
        imgActive:imgHomeActive,
        path:'/'
    },
    {   id:1,
        text:"Lottery",
        imgPic:imgLottery,
        imgActive:imgLotteryActive,
        path:'/lottery'
    },
    {   id:2,
        text:"Listing",
        imgPic:imgList,
        imgActive:imgListActive,
        path:'/listing'
    },
    {   id:3,
        text:"Mine",
        imgPic:imgme,
        imgActive:imgmeActive,
        path:'/mine'
    },
]
export default class footNav extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            index:0,
        }
    }
    render(){
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                {
                    tabbarArr.map((item,index)=>{
                        return(
                            <li key={index} onClick={()=>this.changeFooter(index)}>
                                <Link to={item.path}>
                                    <img src="{imgHome}" alt=""/>
                                    {item.text}
                                </Link>
                            </li>
                        )
                    })
                }       
                </ul>
            </div>
        )
    }
    changeFooter(index){
        this.setState({
            index:index
        })
    }
    // getImgUrl(src){
    //     return require('../../static/images/tabBar/'+src+'.png')
    // }
}
