import React from "react"
import { Carousel, WingBlank, Radio } from 'antd-mobile';
import './index.less'

const slideList = [
    {
      id: "01",
      imgpic: require("./../../../static/images/home/banner1.jpg")
    },
    {
      id: "02",
      imgpic: require("./../../../static/images/home/banner2.jpg")
    },
    {
      id: "03",
      imgpic: require("./../../../static/images/home/banner3.jpg")
    }
  ]
export default class HomeSwiper extends React.Component{
    state = {
        // data: ['1', '2', '3'],
        imgHeight: 176,
      }
      componentDidMount() {
        // simulate img loading
        // setTimeout(() => {
        //   this.setState({
        //     data: ['AiyWuByWklrrUDlFignR', 'TekJlZRVCjLFexlOCuWn', 'IJOtIlfsYdTyaDTRVrLI'],
        //   });
        // }, 100);
      }
    render(){
        return(
            <WingBlank id="swiper_wraps">
            <Carousel
              autoplay={false}
              infinite
              beforeChange={(from, to) => console.log(`slide from ${from} to ${to}`)}
              afterChange={index => console.log('slide to', index)}
            //   style={bottom:'.5rem'}
            dotActiveStyle={{width:'.7rem',height:'.3rem',borderRadius:'.15rem',color:'white'}}
            >
              {
              slideList.map((item,index)=> (
                <a
                  key={index}
                //   href="http://www.alipay.com"
                  style={{ display: 'inline-block', width: '100%', height: this.state.imgHeight }}
                >
                  <img
                    className="wrap_swiper"
                    src={item.imgpic}
                    alt=""
                    style={{ width: '100%', verticalAlign: 'top' }}
                    onLoad={() => {
                      // fire window resize event to change height
                      window.dispatchEvent(new Event('resize'));
                      this.setState({ imgHeight: 'auto' });
                    }}
                  />
                </a>
              ))}
            </Carousel>
          </WingBlank>
        )
    }
}
