import React from 'react';
import ReactDOM from 'react-dom';
import App from './App'
//redux
import {createStore,applyMiddleware} from "redux"
import {composeWithDevTools} from 'redux-devtools-extension'
import logger from "redux-logger"
import thunk from "redux-thunk"
// 国际化
// import { addLocaleData, IntlProvider ,FormattedMessage} from 'react-intl';
// import intl from 'intl';
// import zh from 'react-intl/locale-data/zh';
// import en from 'react-intl/locale-data/en';
// import zh_CN from './locale/zh_CN';
// import en_US from './locale/en_US';

import { Provider } from "react-redux"
import allReducer from "./store/reducers"
import "./static/javascript/rem"
import "./static/css/main.less"
import 'antd-mobile/dist/antd-mobile.css'
// let messages = {};
// messages["zh-CN"] = zh_CN;
// messages["en-US"] = en_US;
// const languages = navigator.languages;
// const currentLang = languages[0];
// console.log("languages: ", languages);
// console.log("language: ", currentLang);
//addLocaleData([...zh,...en]);


//创建store 仓库
const store = createStore(allReducer,{},composeWithDevTools(applyMiddleware(logger,thunk)))// 来存放应用的状态。
// store.subscribe(() => console.log("state",store.getState())) //注册监听器;


ReactDOM.render(
  // <IntlProvider locale={currentLang} messages={messages[currentLang]}>
    <Provider store= {store}>        
      <App/>
    </Provider> 
  // </IntlProvider> 
    , document.getElementById('root')
);
