import React from "react"
import './index.less'
import { Tabs, WhiteSpace,Badge, Progress } from 'antd-mobile';
import content from './../../../static/images/home/content.png'
// import All from './../../../config/basicData/basicData'
const tabs = [
    { title: <Badge >Top</Badge> },
    { title: <Badge>Latest</Badge> },
    { title: <Badge>Chances</Badge> },
    { title: <Badge>Total deman</Badge> },
  ];
  const shopAll = [
    {
      id: "01",
      imgpic: require("./../../../static/images/home/top1.png"),
      total: 14,
      count:7,
      title: "Xiaomi Smartphoneag"
    },
    {
      id: "02",
      imgpic: require("./../../../static/images/home/top2.png"),
      total: 15,
      count:7,
      title: "Xiaomi Smartphoneag lssadsw"
    },
    {
      id: "03",
      imgpic: require("./../../../static/images/home/top3.png"),
      total: 1,
      count:1,
      title: "Xiaomi Smartphoneag"
    },
    {
        id: "04",
        imgpic: require("./../../../static/images/home/top4.png"),
        total: 15,
        count:7,
        title: "Xiaomi Smartphoneag"
      }
  ] 

export default class HomeTab extends React.Component{
    // state = {
    //     percent: 50,
    //   };
    render(){
        // const { percent } = this.state;
        return(
          <div id='Tabs'>
              <div className="whiteSpance">

              </div>
              <Tabs tabs={tabs}
                    initialPage={1}
                    // tabBarBackgroundColor={'red'}
                    tabBarActiveTextColor={'#16C692'}
                    tabBarInactiveTextColor={'#333333'}

                    >
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                       <div className="wrap_list">
                       {
                             shopAll.map((item,index)=>{
                                return (
                                    <div key={index} className="list">
                                       <img src={item.imgpic} alt=""/>
                                       <div className="showEllipsis title">{item.title}</div>
                                       <p>Lottery proccess&nbsp;<span>{Math.round(item.count/item.total*100)}%</span></p>
                                       <p className="wrap_progress">
                                            <div className="progress">
                                                <Progress percent={Math.round(item.count/item.total*100)} position="normal" barStyle={{height:'.5rem'}}/>
                                             </div>
                                             <img src={content} alt=""/>
                                       </p>
                                </div>
                                )
                            })
                       }
                       </div>
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        Content of second tab
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        Content of third tab
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '150px', backgroundColor: '#fff' }}>
                        Content of third tab
                    </div>
              </Tabs>
                    <WhiteSpace />
          </div>
        )
    }
}
