import React from "react"
import { connect } from "react-redux"
// import { increment,decrement } from '../../../store/actions/counter'
import * as counterActions from '../../../store/actions/counter'
import {bindActionCreators} from "redux"
class list extends React.Component{
    render(){
        const { increment,decrement } = this.props
        return(
            <div>
             <h1>{this.props.counter}</h1>
              {/* <button onClick={() =>increment()}>加1</button><br/>
              <button onClick={() =>decrement()}>减1</button><br/> */}
             <button onClick={() =>this.props.counterActions.increment()}>加1</button><br/>
              <button onClick={() =>this.props.counterActions.decrement()}>减1</button><br/>
              {/* <button onClick={
                 }>异步加1</button><br/> */}
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return{ 
        counter:state
    }
}
const mapDispatchToProps = (dispatch) =>{
    return {
       counterActions:bindActionCreators(counterActions,dispatch)
    }
}
// const mapDispatchToProps = (dispatch) =>{
//     return {
