import React from "react"
import {NavLink} from "react-router-dom"
import './footNav.less'
import homeImg from "../../static/images/tabBar/home.png"
import homeActiveImg from "../../static/images/tabBar/home-active.png"
import lotteryImg from "../../static/images/tabBar/lottery.png"
import lotteryActiveImg from "../../static/images/tabBar/lottery-active.png"
import listImg from "../../static/images/tabBar/list.png"
import listActiveImg from "../../static/images/tabBar/list-active.png"
import meImg from "../../static/images/tabBar/me.png"
import meActiveImg from "../../static/images/tabBar/me-active.png"


export default class footNav extends React.Component{
        state = {
            index:1,
        }
        setMenuNum = (num) => {
            console.log(num)
            this.setState({
               index: num
            });
        }    
    render(){
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                    <li>
                        <NavLink to="/" exact onClick= {()=> this.setMenuNum(1)} replace>
                            <img src={homeImg} alt="" className={this.state.index ===1 ? "Nones":"Blocks"} />
                            <img src={homeActiveImg} alt="" className={this.state.index !==1 ? "Nones":"Blocks"}/>
                            <span>Home</span>
                        </NavLink> 
                    </li> 
                    <li >
                        <NavLink to="/lottery" onClick= {()=> this.setMenuNum(2)}>
                            <img src={lotteryImg} alt="" className={this.state.index ===2 ? "Nones":"Blocks"}/>
                            <img src={lotteryActiveImg} alt="" className={this.state.index !==2 ? "Nones":"Blocks"}/>
                            <span>Lottery</span>
                        </NavLink> 
                    </li> 
                    <li>
                        <NavLink to="/listing" onClick= {()=> this.setMenuNum(3)}>
                            <img src={listImg} alt="" className={this.state.index ===3 ? "Nones":"Blocks"}/>
                            <img src={listActiveImg} alt="" className={this.state.index ===3 ? "Blocks":"Nones"}/>
                            <span>Listing</span>
                        </NavLink> 
                    </li> 
                    <li>
                        <NavLink to="/mine" onClick= {()=> this.setMenuNum(4)}>
                            <img src={meImg} alt="" className={this.state.index ===4 ? "Nones":"Blocks"}/>
                            <img src={meActiveImg} alt="" className={this.state.index !==4 ? "Nones":"Blocks"}/>
                            <span>Mine</span>
                        </NavLink> 
                    </li> 
                </ul>
            </div>
        )
    }
    // changeFooter(key){
    //     console.log(key)
    //     this.setState({
    //         index:key
    //     })
    // }
}
