import React from "react"
import {Link} from "react-router-dom"
import { TabBar } from 'antd-mobile';
import './footNav.less'
import homeImg from "../../static/images/tabBar/home.png"
import homeActiveImg from "../../static/images/tabBar/home-active.png"
import lotteryImg from "../../static/images/tabBar/lottery.png"
import lotteryActiveImg from "../../static/images/tabBar/lottery-active.png"
import listImg from "../../static/images/tabBar/list.png"
import listActiveImg from "../../static/images/tabBar/list-active.png"
import meImg from "../../static/images/tabBar/me.png"
import meActiveImg from "../../static/images/tabBar/me-active.png"



export default class footNav extends React.Component{
        state = {
            index:1,
            selectedTab: 'redTab',
            hidden: false,
            fullScreen: false,
            tabList :[
                {
                    title:"Home",
                    key:"Home",
                    icons:{
                        img:homeImg,
                        imgActive:homeActiveImg,
                    },
                    path:"/home"
                },
                {
                    title:"Lottery",
                    key:"Lottery",
                    icons:{
                        img:lotteryImg,
                        imgActive:lotteryActiveImg,
                    },
                    path:"/lottery"
                },
                {
                    title:"Listing",
                    key:"Listing",
                    icons:{
                        img:listImg,
                        imgActive:listActiveImg,
                    },
                    path:"/listing"
                },
                {
                    title:"Mine",
                    key:"Mine",
                    icons:{
                        img:meImg,
                        imgActive:meActiveImg,
                    },
                    path:"/Mine"
                },
            ]
        }
    render(){
        return(
            <div style={this.state.fullScreen ? { position: 'fixed', height: '100%', width: '100%', top: 0 } : { height: 400 }}>
            <TabBar
              unselectedTintColor="#949494"
              tintColor="#33A3F4"
              barTintColor="white"
              hidden={this.state.hidden}
              tabBarPosition="top"
            >
              <TabBar.Item
                title="Life"
                key="Life"
                icon={<div style={{
                  width: '22px',
                  height: '22px',
                  background: 'url(https://zos.alipayobjects.com/rmsportal/sifuoDUQdAFKAVcFGROC.svg) center center /  21px 21px no-repeat' }}
                />
                }
                selectedIcon={<div style={{
                  width: '22px',
                  height: '22px',
                  background: 'url(https://zos.alipayobjects.com/rmsportal/iSrlOTqrKddqbOmlvUfq.svg) center center /  21px 21px no-repeat' }}
                />
                }
                selected={this.state.selectedTab === 'blueTab'}
                badge={1}
                onPress={() => {
                  this.setState({
                    selectedTab: 'blueTab',
                  });
                }}
                data-seed="logId"
              >
                {this.renderContent('Life')}
              </TabBar.Item>
              <TabBar.Item
                icon={
                  <div style={{
                    width: '22px',
                    height: '22px',
                    background: 'url(https://gw.alipayobjects.com/zos/rmsportal/BTSsmHkPsQSPTktcXyTV.svg) center center /  21px 21px no-repeat' }}
                  />
                }
                selectedIcon={
                  <div style={{
                    width: '22px',
                    height: '22px',
                    background: 'url(https://gw.alipayobjects.com/zos/rmsportal/ekLecvKBnRazVLXbWOnE.svg) center center /  21px 21px no-repeat' }}
                  />
                }
                title="Koubei"
                key="Koubei"
                badge={'new'}
                selected={this.state.selectedTab === 'redTab'}
                onPress={() => {
                  this.setState({
                    selectedTab: 'redTab',
                  });
                }}
                data-seed="logId1"
              >
                {this.renderContent('Koubei')}
              </TabBar.Item>
              <TabBar.Item
                icon={
                  <div style={{
                    width: '22px',
                    height: '22px',
                    background: 'url(https://zos.alipayobjects.com/rmsportal/psUFoAMjkCcjqtUCNPxB.svg) center center /  21px 21px no-repeat' }}
                  />
                }
                selectedIcon={
                  <div style={{
                    width: '22px',
                    height: '22px',
                    background: 'url(https://zos.alipayobjects.com/rmsportal/IIRLrXXrFAhXVdhMWgUI.svg) center center /  21px 21px no-repeat' }}
                  />
                }
                title="Friend"
                key="Friend"
                dot
                selected={this.state.selectedTab === 'greenTab'}
                onPress={() => {
                  this.setState({
                    selectedTab: 'greenTab',
                  });
                }}
              >
                {this.renderContent('Friend')}
              </TabBar.Item>
              <TabBar.Item
                icon={{ uri: 'https://zos.alipayobjects.com/rmsportal/asJMfBrNqpMMlVpeInPQ.svg' }}
                selectedIcon={{ uri: 'https://zos.alipayobjects.com/rmsportal/gjpzzcrPMkhfEqgbYvmN.svg' }}
                title="My"
                key="my"
                selected={this.state.selectedTab === 'yellowTab'}
                onPress={() => {
                  this.setState({
                    selectedTab: 'yellowTab',
                  });
                }}
              >
                {this.renderContent('My')}
              </TabBar.Item>
            </TabBar>
          </div>

        )
    }
}
