import React from "react"
import {Link} from "react-router-dom"
import './footNav.less'
const tabbarArr = [
    {   id:0,
        text:"Home",
        imgPic:"home",
        imgActive:require("../../static/images/tabBar/home-active.png"),
        path:'/'
    },
    {   id:1,
        text:"Lottery",
        imgPic:"lottery",
        imgActive:require("../../static/images/tabBar/lottery-active.png"),
        path:'/lottery'
    },
    {   id:2,
        text:"Listing",
        imgPic:"list",
        imgActive:require("../../static/images/tabBar/list-active.png"),
        path:'/listing'
    },
    {   id:3,
        text:"Mine",
        imgPic:"me",
        imgActive:require("../../static/images/tabBar/me-active.png"),
        path:'/mine'
    },
]
export default class footNav extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            index:0,
           tabbarArr:[
                {   id:0,
                    text:"Home",
                    imgPic:"home",
                    imgActive:require("../../static/images/tabBar/home-active.png"),
                    path:'/'
                },
                {   id:1,
                    text:"Lottery",
                    imgPic:"lottery",
                    imgActive:require("../../static/images/tabBar/lottery-active.png"),
                    path:'/lottery'
                },
                {   id:2,
                    text:"Listing",
                    imgPic:"list",
                    imgActive:require("../../static/images/tabBar/list-active.png"),
                    path:'/listing'
                },
                {   id:3,
                    text:"Mine",
                    imgPic:"me",
                    imgActive:require("../../static/images/tabBar/me-active.png"),
                    path:'/mine'
                },
            ]
        }
    }
    render(){
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                {
                    this.props.tabbarArr.map((item,index)=>{
                        return(
                            <li key={index} onClick={()=>this.changeFooter(index)}>
                                <Link to={item.path}>
                                    <img src="{require(`../../static/images/tabBar/lottery-active.png`)}" alt=""/>
                                    {item.text}
                                </Link>
                            </li>
                        )
                    })
                }       
                </ul>
            </div>
        )
    }
    changeFooter(index){
        this.setState({
            index:index
        })
    }
    // getImgUrl(src){
    //     return require('../../static/images/tabBar/'+src+'.png')
    // }
}
