import React from "react"
import {Link} from "react-router-dom"
import './footNav.less'
const tabbarArr = [
    {   id:0,
        text:"Home",
        imgPic:"../../static/images/tabBar/home.png",
        imgActive:require("../../static/images/tabBar/home-active.png"),
        path:'/'
    },
    {   id:1,
        text:"Lottery",
        imgPic:"../../static/images/tabBar/lottery.png",
        imgActive:require("../../static/images/tabBar/lottery-active.png"),
        path:'/lottery'
    },
    {   id:2,
        text:"Listing",
        imgPic:"../../static/images/tabBar/list.png",
        imgActive:require("../../static/images/tabBar/list-active.png"),
        path:'/listing'
    },
    {   id:3,
        text:"Mine",
        imgPic:"../../static/images/tabBar/me.png",
        imgActive:require("../../static/images/tabBar/me-active.png"),
        path:'/mine'
    },
]
export default class footNav extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            index:0,
        }
    }
    render(){
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                {
                    tabbarArr.map((item,index)=>{
                        return(
                            <li key={index} onClick={()=>this.changeFooter(index)}>
                                 <img className="tabImg" src="{require(item.imgPic)}" alt=""/>
                                <Link to={item.path}>

                                    {item.text}
                                </Link>
                            </li>
                        )
                    })
                }       
                </ul>
            </div>
        )
    }
    changeFooter(index){
        this.setState({
            index:index
        })
    }
}
