import React from "react"
import { NavLink } from "react-router-dom"
import './footNav.less'
import homeImg from "../../static/images/tabBar/home.png"
import homeActiveImg from "../../static/images/tabBar/home-active.png"
import lotteryImg from "../../static/images/tabBar/lottery.png"
import lotteryActiveImg from "../../static/images/tabBar/lottery-active.png"
import listImg from "../../static/images/tabBar/list.png"
import listActiveImg from "../../static/images/tabBar/list-active.png"
import meImg from "../../static/images/tabBar/me.png"
import meActiveImg from "../../static/images/tabBar/me-active.png"
// const tabList = [
//     {
//         title:"Home",
//         key:"home",
//         icons:{
//             img:homeImg,
//             imgActive:homeActiveImg,
//         },
//         path:"/"
//     },
//     {
//         title:"Lottery",
//         key:"lottery",
//         icons:{
//             img:lotteryImg,
//             imgActive:lotteryActiveImg,
//         },
//         path:"/lottery"
//     },
//     {
//         title:"Listing",
//         key:"listing",
//         icons:{
//             img:listImg,
//             imgActive:listActiveImg,
//         },
//         path:"/listing"
//     },
//     {
//         title:"Mine",
//         key:"mine",
//         icons:{
//             img:meImg,
//             imgActive:meActiveImg,
//         },
//         path:"/mine"
//     },
// ]
export default class footNav extends React.Component{
    state = {
            selectedTab: 1,  
            hidden:false
            }
    render(){
        return(
            <div className="nav-footer" style={{'display': this.state.hidden ? 'none' : 'block'}}>
                <ul className="clearfix">
                    <li>
                        <NavLink to="/" exact  onClick={(e) => {
                            // e.preventDefault();
                             this.setState({
                                selectedTab: 1,
                           })
                        //    window.loacation.href = '/'
                          }}>
                            {
                                this.state.selectedTab === 1?
                                    <img  src={homeActiveImg} alt=""/> : <img src={homeImg} alt=""/>
                            }
                            <span>Home</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to="/lottery" onClick={(e) => {
                            // e.preventDefault();
                             this.setState({
                                selectedTab: 2,
                           })
                        //    window.loacation.href = '/lottery'
                          }}>
                            {
                                this.state.selectedTab === 2?
                                    <img  src={lotteryActiveImg} alt=""/> : <img src={lotteryImg} alt=""/>
                            }
                            <span>Lottery</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to="/listing" onClick={(e) => {
                            // e.preventDefault();
                             this.setState({
                                selectedTab: 3,
                           })
                        //    window.loacation.href = '/listing'
                          }}>
                            {
                                this.state.selectedTab === 3?
                                    <img  src={listActiveImg} alt=""/> : <img src={listImg} alt=""/>
                            }
                            <span>Listing</span>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink to="/mine" onClick={(e) => {
                            // e.preventDefault();
                             this.setState({
                                selectedTab: 4,
                           })
                        //    window.loacation.href = '/mine'
                          }}>
                            {
                                this.state.selectedTab === 4?
                                    <img  src={meActiveImg} alt=""/> : <img src={meImg} alt=""/>
                            }
                            <span>Mine</span>
                        </NavLink>
                    </li>


                </ul>

            </div>
        )
    }
}
