import React from "react"
import {Link, Route} from "react-router-dom"
import './footNav.less'

export default class footNav extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            index:0,
            tabbarArr : [
                {   id:0,
                    text:"Home",
                    imgPic:require("../../static/images/tabBar/home.png"),
                    imgActive:require("../../static/images/tabBar/home-active.png"),
                    path:'/',
                    isActive:false
                },
                {   id:1,
                    text:"Lottery",
                    imgPic:require("../../static/images/tabBar/lottery.png"),
                    imgActive:require("../../static/images/tabBar/lottery-active.png"),
                    path:'/lottery',
                    isActive:false
                },
                {   id:2,
                    text:"Listing",
                    imgPic:require("../../static/images/tabBar/list.png"),
                    imgActive:require("../../static/images/tabBar/list-active.png"),
                    path:'/listing',
                    isActive:false
                },
                {   id:3,
                    text:"Mine",
                    imgPic:require("../../static/images/tabBar/me.png"),
                    imgActive:require("../../static/images/tabBar/me-active.png"),
                    path:'/mine',
                    isActive:false
                },
            ]
        }
    }
    componentDidMount(){
        let currentPage = window.location.pathname
       console.log(currentPage)
        let data = this.state.tabbarArr
        data.forEach((item,index)=>{
              item.isActive = false
            if(item.path === currentPage){
                data[index].isActive=true;
                this.setState({
                    tabbarArr: data
                  });
            }
        })
    }
    render(){
        let imgPath="";
        let data = this.state.tabbarArr
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                {
                    data.map((item,key)=>{
                        if(item.isActive){
                            imgPath=item.imgActive
                        }else{
                            imgPath=item.imgPic
                        }
                        return(
                            <li key={key} onClick={()=>this.changeFooter(key)}>

                                <Link to={item.path}>
                                    <img src={imgPath}  alt="" />
                                    {/* <img src={item.imgActive}  alt="" /> */}
                                    {/* <img  src={item.imgActive} key={key} alt="" /> */}
                                    {item.text}
                                </Link>
                            </li>
                        )
                    })
                }       
                </ul>
            </div>
        )
    }
    changeFooter(key){
        this.setState({
            index:key
        })
    }
}
