import React from "react"
import { NoticeBar,WingBlank} from 'antd-mobile';
import './index.less'


export default class HomeNoticeBar extends React.Component{
    // state = {
    //     // data: ['1', '2', '3'],
    //     imgHeight: 176,
    //   }

    render(){
        return(
        <div className="homeCente">
            <div id="homeNoticeBar">
                 <WingBlank>
                  <NoticeBar marqueeProps={{ loop: true, style: { padding: '0 7.5px' } }} className="homeNoticeBar_input">
                        Notice: The arrival time of incomes and transfers of Yu &#39;E Bao will be delayed during National Day.
                 </NoticeBar>
                 </WingBlank>
            </div>
            <div className="classify">

            </div>
        </div>

        )
    }
}
