import React from "react"
import intl from 'react-intl-universal'
import { withRouter } from "react-router-dom";
import svg from '../../static/images/svg/logo.svg'
import serch from '../../static/images/index/search.png'
import './index.less'
class indexPageHeader extends React.Component{
    constructor(props){
        super(props);
        //初始化状态
        this.state = {
            liked:false
        };
        //绑定this
        this.handleClick = this.handleClick.bind(this);
    }
    handleClicks(){
        const liked = !this.state.liked;
        this.setState({liked});
    }
    render(){
        const {liked} = this.state;
        return(
            <div id="Wrap">
                <div className="leftSvg">
                    <img src={svg} alt=""/>
                </div>
                <div className="rightWrap">
                    <div className="rightWrapTop">
                        <span onClick={this.handleLanguage.bind(this)}>
                           <span onClick={this.handleClicks}> {liked ?'Janpan':'English'}</span>
                         </span>&nbsp;&nbsp;&nbsp;&nbsp;<span>|</span>&nbsp;&nbsp;&nbsp;&nbsp;
                        <img src={serch} alt="" />
                    </div>
                    <div className="rightWrapBottom">
                        <div> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>
                        <div className="title_T">
                      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                      <a href="https://www.hd.square-enix.com/eng/company/">
                        {intl.get('editor.item.headerTitleO')}
                        </a>
                        </div>
                        <div className="title_T">
                        <a href="https://www.hd.square-enix.com/eng/ir/">
                        {intl.get('editor.item.headerTitleT')}
                        </a>
                        </div>
                        <div className="title_T">
                        <a href="https://www.hd.square-enix.com/eng/group/">
                        {intl.get('editor.item.headerTitleTh')}
                          </a>
                        </div>
                        <div className="title_T"> 
                        <a href="https://www.hd.square-enix.com/eng/news/">
                        {intl.get('editor.item.headerTitleF')}
                        </a>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
Object.assign(indexPageHeader.prototype, {
    componentDidMount() {
      let { location } = this.props
      let ps = this.parseQueryString(location.search)
      let currentLocale = ps.language || 'zh-CN'
      intl.init({
        currentLocale: currentLocale,
        commonLocaleDataUrls: {
          'zh': false,
          'en': false
        },
        locales: {
          [currentLocale]: require(`./../../locales/${currentLocale}`).default
        }
      }).then(() => {
        this.setState({ initDone: true })
      })
    },
    handleLanguage() {
      let { location } = this.props
      let ps = this.parseQueryString(location.search)
      if (ps.language === 'en-US') {
          this.props.history.push('?language=zh-CN')
      } else if (ps.language === 'zh-CN') {
          this.props.history.push('?language=en-US')
      } else {
          this.props.history.push('?language=en-US')
      }

      window.location.reload()
    },
    parseQueryString(url) {
      var params = {};
      var arr = url.split("?");
      if (arr.length <= 1) {
        return params;
      }
      arr = arr[1].split("&");
      for (var i = 0, l = arr.length; i < l; i++) {
        var a = arr[i].split("=");
        params[a[0]] = a[1];
      }
      return params;
    }
  })
  export default withRouter(indexPageHeader)
