import React from "react"
import intl from 'react-intl-universal'
import { withRouter } from "react-router-dom";
import IndexPageBottom from '../indexPage/indexPageBottom'
import thumb1 from '../../static/images/bannar/thumb_other.png'
import './groupPage.less'
class grounpPage extends React.Component{
    render(){
        return(
            <div id="grounpPage">
                {/* 标题 */}
                <div className="grounpPageTitle">
                スクウェア・エニックス・グループについて
                </div>
                {/* 图片 */}
                <div className="grounpPageImg">
                    <img src={thumb1} alt=""/>
                    <div>スクウェア・エニックス・グループについて</div>
                </div> 
                {/* 内容   */}
                <div className="grounpPagecontent">
                    <div>
                    株式会社スクウェア・エニックス・ホールディングスは、多彩なコンテンツ／サービス事業を展開しているスクウェア・エニックス・グループを統括する純粋持株会社です。
                    当社グループは、スクウェア・エニックス、タイトーという国際的ブランドのもと上質なエンターテインメント・コンテンツ／サービスを提供しています。また、先進的な
                    開発拠点を日本、北米および欧州に配置し、国際的な事業推進体制を構築しています。<span>当社グループの自社IPの代表作</span>には「ドラゴンクエスト」シリーズ、「ファイナルフ
                    ァンタジー」シリーズ、「トゥームレイダー」シリーズ、「スペースインベーダー」シリーズなどがあります。
                    </div>
                </div>
                {/* 模块 */}
                <div className="grounpPageModel">
                    <div className="grounpPageCenters">
                        <div className="kuai">
                            <div className="topCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                        <div className="kuai1 kuai">
                        <div className="topCenter">
                        &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                        <div className="kuai2 kuai"> 
                            <div className="topCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                    </div>
                </div>
                <div className="space">
                </div>
                <div className="grounpPageModel">
                    <div className="grounpPageCenters">
                        <div className="kuai">
                            <div className="topCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                        <div className="kuai1 kuai">
                        <div className="topCenter">
                        &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                        <div className="kuai2 kuai"> 
                            <div className="topCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;社長メッセージ
                            </div>
                            <div className="bottomCenter">
                            &nbsp;&nbsp;&nbsp;&nbsp;代表取締役社長 松田洋祐からのメッセージです。
                            </div>
                        </div>
                    </div>
                </div>
                <IndexPageBottom/>
            </div>
        )
    }
}
Object.assign(grounpPage.prototype, {
    componentDidMount() {
      let { location } = this.props
      let ps = this.parseQueryString(location.search)
      let currentLocale = ps.language || 'zh-CN'
      intl.init({
        currentLocale: currentLocale,
        commonLocaleDataUrls: {
          'zh': false,
          'en': false
        },
        locales: {
          [currentLocale]: require(`./../../locales/${currentLocale}`).default
        }
      }).then(() => {
        this.setState({ initDone: true })
      })
    },
    handleLanguage() {
      let { location } = this.props
      let ps = this.parseQueryString(location.search)
      if (ps.language === 'en-US') {
          this.props.history.push('?language=zh-CN')
      } else if (ps.language === 'zh-CN') {
          this.props.history.push('?language=en-US')
      } else {
          this.props.history.push('?language=en-US')
      }

      window.location.reload()
    },
    parseQueryString(url) {
      var params = {};
      var arr = url.split("?");
      if (arr.length <= 1) {
        return params;
      }
      arr = arr[1].split("&");
      for (var i = 0, l = arr.length; i < l; i++) {
        var a = arr[i].split("=");
        params[a[0]] = a[1];
      }
      return params;
    }
   })
  export default withRouter(grounpPage)
