import React from "react"
import {NavLink} from "react-router-dom"
import './footNav.less'

export default class footNav extends React.Component{
    constructor(props){
        super(props)
        this.changeFooter=this.changeFooter.bind(this)
        this.state = {
            index:0,
            ids:"",
            tabbarArr:[
                {   id:0,
                    text:"Home",
                    imgPic:require("../../static/images/tabBar/home.png"),
                    imgActive:require("../../static/images/tabBar/home-active.png"),
                    path:'/',
                    isActive:false
                },
                {   id:1,
                    text:"Lottery",
                    imgPic:require("../../static/images/tabBar/lottery.png"),
                    imgActive:require("../../static/images/tabBar/lottery-active.png"),
                    path:'/lottery',
                    isActive:false
                },
                {   id:2,
                    text:"Listing",
                    imgPic:require("../../static/images/tabBar/list.png"),
                    imgActive:require("../../static/images/tabBar/list-active.png"),
                    path:'/listing',
                    isActive:false
                },
                {   id:3,
                    text:"Mine",
                    imgPic:require("../../static/images/tabBar/me.png"),
                    imgActive:require("../../static/images/tabBar/me-active.png"),
                    path:'/mine',
                    isActive:false
                },
            ]
        }
    }
    // componentDidMount(){
    //     let datas = this.state.tabbarArr
    //     debugger
    //     datas.map((item,index)=>{
    //         // item.isActive = false
    //        item.isActive = false
    //     })
    //     console.log(datas)
    //     let indexs = ''
    //     if(this.state.ids === ''){
    //         indexs = 0
    //     }else{
    //         indexs = this.state.ids
    //     }
    //     datas[indexs].isActive = true
    //     this.setState({
    //         tabbarArr:datas
    //     })
    // }
    render(){
        let datas = this.state.tabbarArr
        return(
            <div className="nav-footer">
                <ul className="clearfix">
                {
                    datas.map((item,index)=>{
                        return(
                            <li key={index} onClick={this.changeFooter(index)} to-data={item.id}>
                                <NavLink to={item.path}>
                                    {/* <img className={item.isActive ? 'Nones' : 'Blocks'} src={item.imgPic}  alt="" /> */}
                                    {/* <img  src={item.imgActive} style={{display: (this.state.tabbarArr[index].isActive === true) ? "block" : "none"}} alt="" /> */}
                                    <img src={this.state.index === index ? item.isActive:item.imgPic} alt="" />/>
                                    {item.text}
                                </NavLink>
                            </li>
                        )
                    })
                }       
                </ul>
            </div>
        )
    }
    changeFooter(index){
        // let id = event.currentTarget.getAttribute("to-data")
        this.setState({
            index:index
        })
    }
}
