import React from 'react'
import { BrowserRouter as Router, Route } from "react-router-dom"
import intl from 'react-intl-universal'
import { withRouter } from "react-router-dom";
import IndexPageHeader from '../src/pages/indexPage/indexPageHeader'
import AppRouter from './router/AppRouter'
class App extends React.Component {
    render () {
        return (           
            <Router> 
                <IndexPageHeader/>  
               {
                  AppRouter.map((item,index)=>{
                    if(item.isShow){
                        if(item.exact){
                            return (
                                <Route
                                key={index}
                                exact
                                path={item.path}
                                render={props=>(
                                    <item.component {...props}/>
                                )}
                                />
                            )
                        }else{
                            return (
                                <Route
                                key={index}
                                path={item.path}
                                render={props=>(
                                    <item.component {...props}/>
                                )}
                                />
                            )
                        }
                    }
                  })   
                }
        </Router> 

        )
    }
}
Object.assign(App.prototype, {
    componentDidMount() {
      let { location } = this.props
      console.log(this.props)
      let ps = this.parseQueryString(location.search)
      let currentLocale = ps.language || 'zh-CN'
      intl.init({
        currentLocale: currentLocale,
        commonLocaleDataUrls: {
          'zh': false,
          'en': false
        },
        locales: {
          [currentLocale]: require(`./locales/${currentLocale}`).default
        }
      }).then(() => {
        this.setState({ initDone: true })
      })
    },
    handleLanguage() {
      let { location } = this.props
      let ps = this.parseQueryString(location.search)
      if (ps.language === 'en-US') {
          this.props.history.push('?language=zh-CN')
      } else if (ps.language === 'zh-CN') {
          this.props.history.push('?language=en-US')
      } else {
          this.props.history.push('?language=en-US')
      }

      window.location.reload()
    },
    parseQueryString(url) {
      var params = {};
      var arr = url.split("?");
      if (arr.length <= 1) {
        return params;
      }
      arr = arr[1].split("&");
      for (var i = 0, l = arr.length; i < l; i++) {
        var a = arr[i].split("=");
        params[a[0]] = a[1];
      }
      return params;
    }
  })
// export default App
export default App
