import React from "react"
import {Link} from "react-router-dom"
import { TabBar } from 'antd-mobile';
import 'antd-mobile@2/dist/antd-mobile.min.css';
import './footNav.less'
import homeImg from "../../static/images/tabBar/home.png"
import homeActiveImg from "../../static/images/tabBar/home-active.png"
import lotteryImg from "../../static/images/tabBar/lottery.png"
import lotteryActiveImg from "../../static/images/tabBar/lottery-active.png"
import listImg from "../../static/images/tabBar/list.png"
import listActiveImg from "../../static/images/tabBar/list-active.png"
import meImg from "../../static/images/tabBar/me.png"
import meActiveImg from "../../static/images/tabBar/me-active.png"



export default class footNav extends React.Component{
        state = {
            index:1,
            selectedTab: 'Home',
            hidden: false,
            fullScreen: false,
            tabList :[
                {
                    title:"Home",
                    key:"Home",
                    icons:{
                        img:homeImg,
                        imgActive:homeActiveImg,
                    },
                    path:"/home"
                },
                {
                    title:"Lottery",
                    key:"Lottery",
                    icons:{
                        img:lotteryImg,
                        imgActive:lotteryActiveImg,
                    },
                    path:"/lottery"
                },
                {
                    title:"Listing",
                    key:"Listing",
                    icons:{
                        img:listImg,
                        imgActive:listActiveImg,
                    },
                    path:"/listing"
                },
                {
                    title:"Mine",
                    key:"Mine",
                    icons:{
                        img:meImg,
                        imgActive:meActiveImg,
                    },
                    path:"/Mine"
                },
            ]
        }
    render(){
        return(
            <div style={this.state.fullScreen ? { position: 'fixed', height: '100%', width: '100%', top: 0 } : { height: 400 }}>
            <TabBar
              unselectedTintColor="#949494"
              tintColor="#33A3F4"
              barTintColor="white"
              hidden={this.state.hidden}
              tabBarPosition="top"
            >
             {
            this.state.tabList.forEach((item,index)=>{
              <TabBar.Item
                title={item.title}
                key={item.key}
                icon={<div style={{
                  width: '22px',
                  height: '22px',
                  background:`url(${item.icons.img}) center center /  21px 21px no-repeat`}}
                />
                }
                selectedIcon={<div style={{
                  width: '22px',
                  height: '22px',
                  background: `url(${item.icons.imgActive}) center center /  21px 21px no-repeat` }}
                />
                }
                selected={this.state.selectedTab === item.key}
                badge={1}
                onPress={() => {
                  this.setState({
                    selectedTab: item.key,
                  });
                }}
                data-seed="logId"
              >
               {this.renderContent(item.title)}
              </TabBar.Item>
                })
            }
            </TabBar>
          </div>

        )
    }
}
